import React, { useState, useEffect } from "react";
import { Button } from "../components/ui/button";
import restaurantList from "../data/restaurantList";

export default function Questionnaire() {
  const [foodType, setFoodType] = useState([]);
  const [cuisine, setCuisine] = useState([]);
  const [aircon, setAircon] = useState([]);
  const [submitted, setSubmitted] = useState(false);
  const [results, setResults] = useState([]);
  const [ratings, setRatings] = useState(() => {
    const saved = localStorage.getItem("restaurantRatings");
    return saved ? JSON.parse(saved) : {};
  });

  const [eaten, setEaten] = useState(() => {
    const saved = localStorage.getItem("eatenRestaurants");
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    localStorage.setItem("restaurantRatings", JSON.stringify(ratings));
  }, [ratings]);

  useEffect(() => {
    localStorage.setItem("eatenRestaurants", JSON.stringify(eaten));
  }, [eaten]);

  const toggleSelection = (value, setter, state) => {
    setter(
      state.includes(value)
        ? state.filter((v) => v !== value)
        : [...state, value]
    );
  };

  const handleSubmit = () => {
    if (foodType.length === 0 || cuisine.length === 0 || aircon.length === 0) {
      setSubmitted(false);
      setResults([]);
      return;
    }

    const filtered = restaurantList.filter((restaurant) => {
      const matchType = foodType.some((type) => restaurant.types.includes(type));
      const matchCuisine = cuisine.some((c) => restaurant.cuisine.includes(c));
      const matchAircon = aircon.includes(restaurant.aircon);
      const notEaten = !eaten.includes(restaurant.name);
      return matchType && matchCuisine && matchAircon && notEaten;
    });

    setResults(filtered);
    setSubmitted(true);
  };

  const handleRating = (restaurantName, ratingValue) => {
    setRatings((prev) => ({
      ...prev,
      [restaurantName]: ratingValue,
    }));
  };

  const handleMarkAsEaten = (restaurantName) => {
    if (!eaten.includes(restaurantName)) {
      setEaten((prev) => [...prev, restaurantName]);
    }
  };

  const ratedRestaurants = restaurantList.filter((r) => eaten.includes(r.name));

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold text-purple-800 mb-6">Find Malaysian Food</h2>

      {/* Food Type */}
      <div className="mb-4">
        <h3 className="font-semibold text-lg mb-2">What do you feel like eating?</h3>
        {['Rice', 'Noodle', 'Dessert'].map((type) => (
          <Button
            key={type}
            variant={foodType.includes(type) ? "default" : "outline"}
            className="m-1"
            onClick={() => toggleSelection(type, setFoodType, foodType)}
          >
            {type}
          </Button>
        ))}
        {submitted && foodType.length === 0 && (
          <p className="text-red-600 text-sm mt-1">Please select at least one food type.</p>
        )}
      </div>

      {/* Cuisine */}
      <div className="mb-4">
        <h3 className="font-semibold text-lg mb-2">Cuisine Preference</h3>
        {['Malay', 'Chinese', 'Indian', 'Mamak'].map((type) => (
          <Button
            key={type}
            variant={cuisine.includes(type) ? "default" : "outline"}
            className="m-1"
            onClick={() => toggleSelection(type, setCuisine, cuisine)}
          >
            {type}
          </Button>
        ))}
        {submitted && cuisine.length === 0 && (
          <p className="text-red-600 text-sm mt-1">Please select at least one cuisine.</p>
        )}
      </div>

      {/* Aircon */}
      <div className="mb-6">
        <h3 className="font-semibold text-lg mb-2">Air Conditioning</h3>
        {['AC', 'No AC'].map((type) => (
          <Button
            key={type}
            variant={aircon.includes(type) ? "default" : "outline"}
            className="m-1"
            onClick={() => toggleSelection(type, setAircon, aircon)}
          >
            {type}
          </Button>
        ))}
        {submitted && aircon.length === 0 && (
          <p className="text-red-600 text-sm mt-1">Please select your aircon preference.</p>
        )}
      </div>

      <Button className="mb-6" onClick={handleSubmit}>
        Show Recommendations
      </Button>

      {/* Recommended Results */}
      {submitted && results.length > 0 && (
        <div className="mt-6">
          <h3 className="text-xl font-bold text-purple-700 mb-4">Recommended Restaurants:</h3>
          <ul className="space-y-6">
            {results.map((rest) => (
              <li key={rest.name} className="bg-white shadow rounded-lg p-4">
                <h4 className="text-lg font-bold text-purple-800">{rest.name}</h4>
                <p className="text-gray-700">{rest.description}</p>
                <p className="text-sm text-gray-600 mt-1">Hours: {rest.hours}</p>
                <a
                  href={rest.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 text-sm underline"
                >
                  View on Google Maps
                </a>
                <p className="mt-3 text-sm text-gray-700">
                  <strong>Rating:</strong>{" "}
                  {ratings[rest.name] ? `${ratings[rest.name]} / 5` : "Not rated"}
                </p>
                <div className="mt-2 flex gap-2">
                  {[1, 2, 3, 4, 5].map((num) => (
                    <button
                      key={num}
                      onClick={() => handleRating(rest.name, num)}
                      className={`w-8 h-8 rounded-full text-white text-sm ${
                        ratings[rest.name] === num
                          ? "bg-purple-700"
                          : "bg-purple-300 hover:bg-purple-500"
                      }`}
                    >
                      {num}
                    </button>
                  ))}
                </div>
                <Button
                  className="mt-3 text-xs"
                  variant="outline"
                  onClick={() => handleMarkAsEaten(rest.name)}
                >
                  I Ate Here
                </Button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Eaten Section */}
      {ratedRestaurants.length > 0 && (
        <div className="mt-12">
          <h3 className="text-xl font-bold text-purple-700 mb-4">Your Rated Restaurants:</h3>
          <ul className="space-y-6">
            {ratedRestaurants.map((rest) => (
              <li key={rest.name} className="bg-gray-100 shadow rounded-lg p-4">
                <h4 className="text-lg font-bold text-purple-800">{rest.name}</h4>
                <p className="text-gray-700">{rest.description}</p>
                <p className="text-sm text-gray-600 mt-1">Hours: {rest.hours}</p>
                <a
                  href={rest.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 text-sm underline"
                >
                  View on Google Maps
                </a>
                <p className="mt-3 text-sm text-gray-700">
                  <strong>Your Rating:</strong>{" "}
                  {ratings[rest.name] ? `${ratings[rest.name]} / 5` : "Not rated"}
                </p>
              </li>
            ))}
          </ul>
        </div>
      )}

      {submitted && results.length === 0 && (
        <p className="text-red-600 font-medium mt-6">No matching restaurants found. Try different preferences.</p>
      )}
    </div>
  );
}
