import React, { useState } from "react";
import { Button } from "../components/ui/button";
import foodList from "../data/foodList"; // You‚Äôll create this later

export default function Questionnaire() {
  const [answers, setAnswers] = useState({
    foodType: "",
    cuisine: "",
    aircon: "",
  });

  const [results, setResults] = useState([]);

  const handleChange = (field, value) => {
    setAnswers((prev) => ({ ...prev, [field]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const filtered = foodList.filter((food) => {
      const matchType = answers.foodType === "any" || food.type === answers.foodType;
      const matchCuisine = answers.cuisine === "any" || food.cuisine === answers.cuisine;
      const matchAircon = answers.aircon === "any" || food.aircon === answers.aircon;
      return matchType && matchCuisine && matchAircon;
    });

    setResults(filtered);
  };

  const isComplete = answers.foodType && answers.cuisine && answers.aircon;

  const getButtonClass = (field, option) => {
    const selected = answers[field] === option;
    return `px-4 py-2 rounded-xl text-sm transition-all ${
      selected
        ? "bg-purple-700 text-white"
        : "bg-white border border-purple-300 text-purple-700 hover:bg-purple-100"
    }`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 to-purple-300 p-6 flex flex-col items-center">
      <form
        onSubmit={handleSubmit}
        className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-xl space-y-8"
      >
        <h2 className="text-3xl font-bold text-purple-700 text-center">
          üçΩÔ∏è What are you in the mood for?
        </h2>

        {/* Food Type */}
        <div>
          <label className="block text-lg font-medium mb-2">üçö Rice or Noodles?</label>
          <div className="flex flex-wrap gap-2">
            {["rice", "noodle", "any"].map((option) => (
              <Button
                key={option}
                type="button"
                className={getButtonClass("foodType", option)}
                onClick={() => handleChange("foodType", option)}
              >
                {option === "any" ? "Anything" : option.charAt(0).toUpperCase() + option.slice(1)}
              </Button>
            ))}
          </div>
        </div>

        {/* Cuisine */}
        <div>
          <label className="block text-lg font-medium mb-2">üåè Cuisine Preference?</label>
          <div className="flex flex-wrap gap-2">
            {["chinese", "malay", "indian", "mamak", "any"].map((option) => (
              <Button
                key={option}
                type="button"
                className={getButtonClass("cuisine", option)}
                onClick={() => handleChange("cuisine", option)}
              >
                {option === "any" ? "Anything" : option.charAt(0).toUpperCase() + option.slice(1)}
              </Button>
            ))}
          </div>
        </div>

        {/* Aircon */}
        <div>
          <label className="block text-lg font-medium mb-2">‚ùÑÔ∏è Air-conditioning?</label>
          <div className="flex flex-wrap gap-2">
            {["yes", "no", "any"].map((option) => (
              <Button
                key={option}
                type="button"
                className={getButtonClass("aircon", option)}
                onClick={() => handleChange("aircon", option)}
              >
                {option === "yes" ? "Air-conditioned" : option === "no" ? "No Aircon" : "Anything"}
              </Button>
            ))}
          </div>
        </div>

        {/* Submit */}
        <div className="text-center pt-4">
          <Button
            type="submit"
            className="px-6 py-3 text-lg bg-purple-700 text-white hover:bg-purple-800"
            disabled={!isComplete}
          >
            Show My Food üç±
          </Button>
        </div>
      </form>

      {/* Results */}
      {results.length > 0 && (
        <div className="mt-10 w-full max-w-3xl grid md:grid-cols-2 gap-6">
          {results.map((food, idx) => (
            <div key={idx} className="bg-white shadow-lg rounded-xl overflow-hidden">
              <img src={food.image} alt={food.name} className="w-full h-48 object-cover" />
              <div className="p-4">
                <h3 className="text-xl font-bold text-purple-700">{food.name}</h3>
                <p className="text-gray-600">{food.description}</p>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* No Match */}
      {results.length === 0 && isComplete && (
        <div className="text-center mt-8 text-red-600 font-semibold">
          No matching food found üò¢
        </div>
      )}
    </div>
  );
}
