import React, { useState } from "react";
import { Button } from "../components/ui/button";
import foodList from "../data/foodList";

export default function Questionnaire() {
  const [answers, setAnswers] = useState({
    foodType: [],
    cuisine: [],
    aircon: [],
  });

  const [results, setResults] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  const isComplete =
    answers.foodType.length > 0 &&
    answers.cuisine.length > 0 &&
    answers.aircon.length > 0;

  const toggleOption = (field, value) => {
    setAnswers((prev) => {
      const current = prev[field];
      return {
        ...prev,
        [field]: current.includes(value)
          ? current.filter((v) => v !== value)
          : [...current, value],
      };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!isComplete) {
      // Prevent submission if incomplete
      return;
    }

    const filtered = foodList.filter((food) => {
      const matchType = answers.foodType.includes(food.type);
      const matchCuisine = answers.cuisine.includes(food.cuisine);
      const matchAircon = answers.aircon.includes(food.aircon);
      return matchType && matchCuisine && matchAircon;
    });

    setResults(filtered);
    setSubmitted(true); // Only mark submitted after filtering
  };

  const buttonStyle = (active) =>
    `px-4 py-2 rounded-lg font-semibold transition-all duration-200 ${
      active
        ? "bg-purple-700 text-white shadow-md ring-2 ring-purple-500 scale-105"
        : "bg-purple-200 text-purple-800 border border-purple-400 hover:bg-purple-300"
    }`;

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 to-purple-300 p-6 flex flex-col items-center">
      <form
        onSubmit={handleSubmit}
        className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-xl space-y-8"
      >
        <h2 className="text-3xl font-bold text-purple-700 text-center">
          🍽️ What are you in the mood for?
        </h2>

        {/* Food Type */}
        <div>
          <label className="block text-lg font-medium mb-2">🍚 Rice or Noodles?</label>
          <div className="flex flex-wrap gap-2">
            {["rice", "noodle"].map((option) => (
              <Button
                key={option}
                type="button"
                className={buttonStyle(answers.foodType.includes(option))}
                onClick={() => toggleOption("foodType", option)}
              >
                {option.charAt(0).toUpperCase() + option.slice(1)}
              </Button>
            ))}
          </div>
        </div>

        {/* Cuisine */}
        <div>
          <label className="block text-lg font-medium mb-2">🌏 Cuisine Preference?</label>
          <div className="flex flex-wrap gap-2">
            {["chinese", "malay", "indian", "mamak"].map((option) => (
              <Button
                key={option}
                type="button"
                className={buttonStyle(answers.cuisine.includes(option))}
                onClick={() => toggleOption("cuisine", option)}
              >
                {option.charAt(0).toUpperCase() + option.slice(1)}
              </Button>
            ))}
          </div>
        </div>

        {/* Aircon */}
        <div>
          <label className="block text-lg font-medium mb-2">❄️ Air-conditioning?</label>
          <div className="flex flex-wrap gap-2">
            {["yes", "no"].map((option) => (
              <Button
                key={option}
                type="button"
                className={buttonStyle(answers.aircon.includes(option))}
                onClick={() => toggleOption("aircon", option)}
              >
                {option === "yes" ? "Air-conditioned" : "No Aircon"}
              </Button>
            ))}
          </div>
        </div>

        {/* Submit Button */}
        <div className="text-center pt-4">
          <Button
            type="submit"
            className="px-6 py-3 text-lg"
            disabled={!isComplete}
          >
            Show My Food 🍱
          </Button>
        </div>
      </form>

      {/* Show Results After Submission */}
      {submitted && isComplete && (
        <>
          {results.length > 0 ? (
            <div className="mt-10 w-full max-w-3xl grid md:grid-cols-2 gap-6">
              {results.map((food, idx) => (
                <div
                  key={idx}
                  className="bg-white shadow-lg rounded-xl overflow-hidden"
                >
                  <img
                    src={food.image}
                    alt={food.name}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-4">
                    <h3 className="text-xl font-bold text-purple-700">{food.name}</h3>
                    <p className="text-gray-600">{food.description}</p>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center mt-8 text-red-600 font-semibold">
              No matching food found 😢
            </div>
          )}
        </>
      )}
    </div>
  );
}
