import React, { useState } from "react";
import { Button } from "../components/ui/button";

const foodList = [
  {
    name: "Nasi Lemak",
    type: "rice",
    cuisine: "malay",
    aircon: "no",
    description: "A fragrant coconut milk rice dish with sambal and fried anchovies.",
    image: "https://www.recipetineats.com/wp-content/uploads/2016/05/Nasi-Lemak_0.jpg"
  },
  {
    name: "Char Kway Teow",
    type: "noodle",
    cuisine: "chinese",
    aircon: "no",
    description: "Stir-fried flat rice noodles with prawns, eggs, and bean sprouts.",
    image: "https://www.rotinrice.com/wp-content/uploads/2011/10/CharKwayTeow.jpg"
  },
  {
    name: "Roti Canai",
    type: "bread",
    cuisine: "mamak",
    aircon: "no",
    description: "Crispy and fluffy flatbread served with dhal or curry.",
    image: "https://www.kuali.com/wp-content/uploads/2019/04/roti-canai-1.jpg"
  },
  {
    name: "Banana Leaf Rice",
    type: "rice",
    cuisine: "indian",
    aircon: "yes",
    description: "Traditional Indian rice meal served on banana leaves with various curries.",
    image: "https://sethlui.com/wp-content/uploads/2017/04/Banana-Leaf-Rice-KL-Sri-Nirwana-Maju-4.jpg"
  }
];

export default function Questionnaire() {
  const [answers, setAnswers] = useState({
    foodType: [],
    cuisine: [],
    aircon: [],
  });
  const [submittedAnswers, setSubmittedAnswers] = useState(null);
  const [results, setResults] = useState([]);
  const [error, setError] = useState("");

  const toggleSelection = (question, value) => {
    setAnswers((prev) => {
      const current = prev[question];
      const isSelected = current.includes(value);
      const updated = isSelected
        ? current.filter((item) => item !== value)
        : [...current, value];
      return { ...prev, [question]: updated };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (
      answers.foodType.length === 0 ||
      answers.cuisine.length === 0 ||
      answers.aircon.length === 0
    ) {
      setError("Please select at least one option from each category (Food Type, Cuisine, and Air Conditioning).");
      return;
    }

    setError("");
    setSubmittedAnswers({ ...answers });

    const filtered = foodList.filter((food) => {
      const matchType = answers.foodType.includes(food.type);
      const matchCuisine = answers.cuisine.includes(food.cuisine);
      const matchAircon = answers.aircon.includes(food.aircon);
      return matchType && matchCuisine && matchAircon;
    });

    setResults(filtered);
  };

  const renderOptions = (question, options) => (
    <div className="flex flex-wrap gap-3">
      {options.map((opt) => {
        const isSelected = answers[question].includes(opt.value);
        return (
          <button
            key={opt.value}
            type="button"
            onClick={() => toggleSelection(question, opt.value)}
            className={`px-5 py-2 rounded-full border transition-all ${
              isSelected
                ? "bg-purple-700 text-white border-purple-700"
                : "bg-white text-purple-700 border-purple-300 hover:bg-purple-100"
            }`}
          >
            {opt.label}
          </button>
        );
      })}
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 to-purple-300 p-6 flex flex-col items-center">
      <form
        onSubmit={handleSubmit}
        className="bg-white/80 backdrop-blur-lg shadow-2xl p-10 rounded-2xl max-w-3xl w-full space-y-8"
      >
        <h1 className="text-4xl font-bold text-center text-purple-800">🍱 Food Preferences</h1>

        {/* Food Type */}
        <div>
          <h2 className="text-xl font-semibold mb-3">What are you feeling like eating?</h2>
          {renderOptions("foodType", [
            { label: "Rice", value: "rice" },
            { label: "Noodle", value: "noodle" },
            { label: "Bread", value: "bread" },
          ])}
        </div>

        {/* Cuisine */}
        <div>
          <h2 className="text-xl font-semibold mb-3">What type of cuisine?</h2>
          {renderOptions("cuisine", [
            { label: "Malay", value: "malay" },
            { label: "Chinese", value: "chinese" },
            { label: "Indian", value: "indian" },
            { label: "Mamak", value: "mamak" },
          ])}
        </div>

        {/* Aircon */}
        <div>
          <h2 className="text-xl font-semibold mb-3">Do you prefer air-conditioned places?</h2>
          {renderOptions("aircon", [
            { label: "Yes", value: "yes" },
            { label: "No", value: "no" },
          ])}
        </div>

        {/* Submit Button */}
        <div className="text-center pt-4">
          <Button
            type="submit"
            className="px-6 py-3 text-lg"
          >
            Show My Food 🍜
          </Button>
        </div>

        {/* Error Message */}
        {error && (
          <div className="text-center text-red-600 font-medium pt-2">{error}</div>
        )}
      </form>

      {/* Results */}
      {submittedAnswers && (
        <div className="mt-10 w-full max-w-3xl">
          {results.length > 0 ? (
            <div className="grid md:grid-cols-2 gap-6">
              {results.map((food, idx) => (
                <div key={idx} className="bg-white shadow-xl rounded-xl overflow-hidden">
                  <img
                    src={food.image}
                    alt={food.name}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-4">
                    <h3 className="text-xl font-bold text-purple-700">{food.name}</h3>
                    <p className="text-gray-600">{food.description}</p>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center mt-8 text-red-600 font-semibold">
              No matching food found 😢
            </div>
          )}
        </div>
      )}
    </div>
  );
}
